package main;

public class if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String b = "Sinh viên" ;
		if (b == "Học sinh")
			System.out.println("Bạn học cấp 1,2,3");
		else if (b == "Sinh viên" )
			System.out.println("Bạn học đại học");
			else
				System.out.println("Không rõ nghề nghiệp");
	}

}
